hooks.hook:

{ia_add_media files='js:_IA_URL_plugins/adblockdetector/js/blockadblock,js:_IA_URL_plugins/adblockdetector/js/bootstrap-notify.min'}
{ia_add_js}
function adBlockDetected()
{
	$.notify({
		// options
		message: _t('adblockdetector_content'),
	}, {
		type: 'danger',
		allow_dismiss: false,
		'placement.align': 'center',
		delay: 20000,
	});
}

$(function()
{
	(typeof blockAdBlock === 'undefined') ? adBlockDetected() : blockAdBlock.onDetected(adBlockDetected);
});
	{/ia_add_js}
